import React, { useEffect, useState } from 'react'
import { Modal, message } from 'antd';
import Loading from '../../loading';
import { getData } from '../../resources';

export default function ReportsDoctors({ id_medico, isModalOpen, handleOk }) {

    const [loading, setLoading] = useState(true)
    const [citasData, setCitasData] = useState(null)

    useEffect(() => { getCitasData() }, [isModalOpen])

 
    

    const getCitasData = () => {
        setLoading(true)
        if(id_medico) {
            getData(`citas/medico/${id_medico}`).then(rs => {
                setCitasData(rs)
                setLoading(false)
            })
        } else {
            message.error("Sin datos del medico")
        }
    }

    return (
        <Modal title="Basic Modal" open={isModalOpen} onOk={handleOk} onCancel={handleOk} destroyOnClose>
            {
                loading ? <Loading /> :
                <div>



                    <p>Id medico {id_medico}</p>
                    { // GO TO BACKEND AND MAKE A QUERY TO FILTER CITAS BY DATE
                        JSON.stringify(citasData)
                    }



                </div>
            }
        </Modal>
    )
}
