import React, { useEffect } from 'react'
import { Modal } from 'antd'
import { Page, Text, View, Document, StyleSheet, Image, Font } from '@react-pdf/renderer';
import { PDFViewer } from '@react-pdf/renderer';

import Arial from '../../assets/fonts/arial/arial.ttf'

import Logo from '../../assets/Logo.png'
import { usuario, IMAGE_API } from '../../resources';

function ExoneracionDocument({ pacienteData, expedienteData, historia, notas, recetas }) {

    useEffect(() => {
        // console.log('receta props', props)
    }, [])

    Font.register({ family: 'Arial', src: Arial });

    const styles = StyleSheet.create({
        page: {
            flexDirection: 'column',
            backgroundColor: 'white',
            padding: 40
        },
    });

    // Create Document Component
    function Receta() {
        return <Document>
            <Page size="A4" orientation="vertical" style={styles.page}>

                <view >
                    <Text>CARTA DE EXONERACION</Text>
                </view>
                <View >

                    <Text style={{ fontSize: 12, fontFamily: 'Arial' }}>El que suscribe, en pleno uso de mis facultades mentales y con plena ausencia de vicios de la voluntad, ASUMO TODOS LOS RIESGOS DE PARTICIPAR EN CUALRQUIERA/TODAS LAS TERAPIAS Y TRATAMIENTOS llevadas a cabo por el cuerpo clínico de CDEFIS (Centro del Deporte Fisioterapia y Salud)  ubicado en Avenida Lázaro Cárdenas #2707 dos mil setecientos siete, colonia Chapultepec Norte, Morelia Michoacán comprendidas en: VENDAJE NEUROMUSCULAR, VENTOSAS TERAPEUTICAS, LIBREACION MIOFASCIAL, DESCARGA MUSCULAR, MASAJE DEPORTIVO, ELECTROTERAPIA, PUNCION SECA, FORTALECIMIENTO MUSCULAR; TRATAMIENTOS PARA HERNIA DISCAL, NERVIO CIATICO, ESCOLIOSIS; FISIOTERAPIA PRE Y POST OPERATORIA DE COLUMNA, HOMBRO, RODILLA, CADERA,ETC; READAPTACION DEPORTIVA, ECOGRAFIA DIAGNOSTICA, UTLRASONIDO TERAPEUTICO, TRABAJO ISOINERCIAL, TERAPIA MANUAL, TERAPUA MANUAL INSTRUMENTALIZADA; TRATAMIENTOS CON ALTA FRECUENCIA RADIOFRECUENCIA CAVITACION, DERMAPEN, HIFU, ELECTROLIPOSIS, SPA, GALVANICO FACIAL, CORPORAL. Incluyendo a modo de ejemplo y sin limitación cualquier riesgo que pueda surgir por negligencia o descuido por parte de las personas liberadas, por equipos o bienes peligrosos o defectuosos, que sean de su propiedad mantenidos o controlados por ellos o por su posible responsabilidad sin culpa
                    </Text>
                    <View style={{ height: 12 }}></View>

                    <Text style={{ fontSize: 12 }}>
                        CERTIFICO que conozco y asumo los riesgos y contradicciones médicas que los tratamientos antes mencionados acarrean, así como DECLARO no tener alergias a ninguno de los productos, tratamientos y métodos a los cuales me veré sometido; así como, autorizo que se me realicen los tratamientos antes mencionados
                    </Text>
                    <View style={{ height: 12 }}></View>

                    <Text>
                        EXONERO totalmente de responsabilidades a la Clínica CDEFIS, así como personal administrativo y clínico, de toda culpa, responsabilidad civil y social por cualquier riesgo que pueda surgir por negligencia o descuido por parte de las personas liberadas, por equipos o bienes peligrosos o defectuosos, que sean de su propiedad mantenidos o controlados por ellos o por su posible responsabilidad sin culpa. </Text>
                </View>
                <View >
                    <Text>Nombre, Fecha y firma del paciente</Text>
                </View>
            </Page>


        </Document>
    }
    return <PDFViewer height={600} width={820}>
        <Receta />
    </PDFViewer>

}



export default function Exoneracion({ isModalOpen, setIsModalOpen }) {


    const handleOk = () => {
        setIsModalOpen(false);
    };
    const handleCancel = () => {
        setIsModalOpen(false);
    };

    return (
        <Modal title="Basic Modal" open={isModalOpen} onOk={handleOk} onCancel={handleCancel} width={900}>
            <ExoneracionDocument />
        </Modal>
    )
}
