function fillZero(digit) {
  if (Math.abs(digit) > 10) return String(digit);
  return digit.toString().padStart(2, '0');
}
export function dynamicFormatDateTime(date) {
  const year = date.getFullYear();
  const month = fillZero(date.getMonth() + 1);
  const day = fillZero(date.getDate());
  const yyyyMMDD = "".concat(year, "-").concat(month, "-").concat(day);
  const hour = date.getHours();
  const minutes = date.getMinutes();
  const seconds = date.getSeconds();
  if (hour || minutes || seconds) return "".concat(yyyyMMDD, " ").concat(fillZero(hour), ":").concat(fillZero(minutes), ":").concat(fillZero(seconds));
  return yyyyMMDD;
}
