import React, { useEffect, useState } from 'react'
import { Select, Form, message, Table, Button } from 'antd'
import { getData, ids_hospitales, sendDataBody } from '../../resources'
import Loading from '../../loading'
import { render } from '@testing-library/react'

export default function PatientsResults() {

    const [loading, setLoading] = useState(true)
    const [allPacientes, setAllPacientes] = useState([])
    const [selectedPatient, setSelectedPatient] = useState(null)
    const [patientScalas, setPatientScalas] = useState(null)
    const [medicos, setMedicos] = useState([]) // List of medicos able to select


    useEffect(() => {
        const getAllMedicos = async () => {
            await getData(`users/hospital/${ids_hospitales[0]}`).then(rs => { getAllPacientes(rs) })
        }

        getAllMedicos()
        // usuario.rol === 'Medico' ? getPacientesOfDoctor(usuario._id) : getAllMedicos()

    }, [])

    const getAllPacientes = async (medicos) => {
        const medicosFormatted = medicos.map(m => m._id)
        await sendDataBody('pacientes/medicos', { medicos: medicosFormatted }).then(rs => { setAllPacientes(rs); setLoading(false) })
    }


    // Form Methods
    const handlePatientChange = (value) => {
        console.log('Selected Patient ', value)
        setSelectedPatient(value)
        // const found = allPacientes.find((p) => p._id === value);
        // if (found) {

        //     let { medicos_asignados } = found
        //     medicos_asignados.forEach((m) => {
        //         m.label = m.name; m.value = m._id
        //     });

        //     setMedicos(medicos_asignados);

        // } else {
        //     message.error('Paciente no encontrado')
        // }
        getPatientScalas(value)
    };


    const getPatientScalas = (id) => {
        getData(`encuestas/paciente/${id}`).then(rs => { console.log(rs); setPatientScalas(rs) })
    }

    const filterOption = (input, option) =>
        (option?.label ?? '').toLowerCase().includes(input.toLowerCase());

    const DepresionResistenteTable = () => {

        const searchEscala = (esc) => {
            const { protocolo, tipo, momento } = esc;
            console.log('Searching', esc);
            const found = patientScalas.find((scala) => { return scala.tipo === tipo && scala.cat[0] === protocolo && scala.cat[1] === momento })
            if (found) message.info('Esa mero')
            else message.error('Escala aún no registrada')
        }

        const dataSource = [
            {
                key: '1',
                name: { label: 'Sociodemografico', value: 'Sociodemografico' },
                age: { label: 'GAD7', value: 'gad_7' },
                address: { label: 'GAD7', value: 'gad_7' },
                address2:{ label: 'GAD7', value: 'gad_7' },
            },
            {
                key: '2',
                name: { label: 'GAD7', value: 'gad_7' },
                age: { label: 'PHQ9P', value: 'phq9p' },
                address: { label: 'PHQ9P', value: 'phq9p' },
                address2: { label: 'PHQ9P', value: 'phq9p' },
            },
            {
                key: '3',
                name: { label: 'PHQ9P', value: 'phq9p' },
                age: { label: 'Satisfaccion', value: 'satisfaccion' }
            },
            {
                key: '4',
                name: { label: 'CSSRS', value: 'cssrs' },
                age: { label: 'CSSRS', value: 'cssrs' }
            },
        ];

        const columns = [
            {
                title: 'Aplicación inicial',
                dataIndex: 'name',
                key: 'name',
                render: (_, { name }) => <Button onClick={() => searchEscala({ protocolo: 'depreres', tipo: name.value, momento: 'inicial' })} type='link'>{name.label}</Button>
            },
            {
                title: 'Aplicación final',
                dataIndex: 'age',
                key: 'age',
                render: (_, { age }) => <Button onClick={() => searchEscala('depreres' + age.value + 'final')} type='link'>{age.label}</Button>
            },
            {
                title: 'Inicio de mantenimiento',
                dataIndex: 'address',
                key: 'address',
                render: (_, { address }) => <Button onClick={() => searchEscala('depreres' + address.value + 'mant1')} type='link'>{address.label}</Button>
            },
            {
                title: 'Final mantenimiento',
                dataIndex: 'address2',
                key: 'address2',
                render: (_, { address2 }) => <Button onClick={() => searchEscala('depreres' + address2.value + 'mant2')} type='link'>{address2.label}</Button>
            },
        ];


        return <div>
            <h4>Escalas EMT</h4>
            <h6>Depresion resistente al tratamiento</h6>
            <Table dataSource={dataSource} columns={columns} />;

        </div>
    }
    const TinnitusTable = () => {

        const searchEscala = (esc) => {
            message.info('Searching ' + esc)
        }

        const dataSource = [
            {
                key: '1',
                name: 'Sociodemografico',
                age: 'GAD7',
            },
            {
                key: '2',
                name: 'GAD7',
                age: 'PHQ9P',
            },
            {
                key: '3',
                name: 'PHQ9P',
                age: 'THI'
            },
            {
                key: '4',
                name: 'THI',
                age: 'Satisfaccion'
            },
        ];

        const columns = [
            {
                title: 'Aplicación inicial',
                dataIndex: 'name',
                key: 'name',
                render: (_, { name }) => <Button onClick={() => searchEscala('tinnitus' + name + 'inicial')} type='link'>{name}</Button>
            },
            {
                title: 'Aplicación final',
                dataIndex: 'age',
                key: 'age',
                render: (_, { age }) => <Button onClick={() => searchEscala('tinnitus' + age + 'final')} type='link'>{age}</Button>
            }
        ];


        return <div>
            <h6>TINNITUS</h6>
            <Table dataSource={dataSource} columns={columns} />;

        </div>
    }


    if (loading) return <Loading />;

    return (
        <div className='mainContainer'>

            <h5>Resultados de escalas de los diferentes pacientes</h5>

            <Form.Item label="Paciente" name="usuario" rules={[{ required: true, message: 'Selecciona el paciente' }]}
                style={{ alignItems: 'center', paddingTop: 20 }}
                labelCol={{ span: 6 }} wrapperCol={{ span: 18 }}>

                <Select
                    showSearch
                    filterOption={filterOption}
                    onChange={handlePatientChange}
                    placeholder='Selecciona un paciente'
                    value={selectedPatient}
                    options={
                        allPacientes.map(p => (
                            {
                                ...p,
                                key: p._id,
                                value: p._id,
                                label: p.name
                            }
                        ))
                    }
                />
            </Form.Item>


            {
                patientScalas ?
                    <div>
                        <div>Escalas de {JSON.stringify(selectedPatient)} {JSON.stringify(patientScalas)}</div>
                        <DepresionResistenteTable />
                        <TinnitusTable />
                    </div> :
                    <p>Seleccione un paciente de la lista de arriba</p>
            }

        </div>
    )
}
