import React, { useRef, useState, useEffect } from "react";
import "./PainZoneSelector.css";

const PainZoneSelector = () => {
  const canvasRef = useRef(null);
  const [point, setPoint] = useState(null);

  const handleClick = (event) => {
    const rect = event.target.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;
    setPoint({ x, y });
  };

  const drawCircle = (context, point) => {
    if (point) {
      context.clearRect(0, 0, context.canvas.width, context.canvas.height);
      context.beginPath();
      context.arc(point.x, point.y, 10, 0, 2 * Math.PI);
      context.fillStyle = "rgba(255, 0, 0, 0.5)";
      context.fill();
      context.stroke();
    }
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    drawCircle(context, point);
  }, [point]);

  const handleSave = () => {
    // Guardar las coordenadas en la base de datos
    console.log("Selected Point:", point);
  };

  return (
    <div className="pain-zone-selector">
      <h3>Seleccione su zona de dolor</h3>
      <div className="canvas-container">
        <img
          src="../../assets/head.jpg"
          alt="Cuerpo humano"
          className="background-image"
          onLoad={() => {
            const canvas = canvasRef.current;
            const context = canvas.getContext("2d");
            drawCircle(context, point);
          }}
        />
        <canvas
          ref={canvasRef}
          width={800}
          height={600}
          onClick={handleClick}
        />
      </div>
      <button onClick={handleSave}>Guardar</button>
    </div>
  );
};

export default PainZoneSelector;
